<script type="text/javascript">
    jQuery( document ).ready(function() {

        jQuery("#btnSuscribeNewsletter").click(abreNewsSuscription);
        jQuery("#popUpNewsletterWrapper").bind("click",cierraPopNewsletter);
        jQuery("#btnCerrarNewsletter").bind("click",cierraPopNewsletter);
        jQuery("#btnCerrarNewsletter_2").bind("click",cierraPopNewsletter);
        jQuery("#popNewsletterClose").bind("click",cierraPopNewsletter);
        jQuery("#btnSuscribirNewsletter").bind("click", validaSuscripcion );

        function validaSuscripcion(){
            var errorTxt = "";
            var email = jQuery("#edit-mergevars-email").val();

            if( !esMail(email) ){
                errorTxt += "- Debe introducir una dirección de e-mail válida<br>";
            }

            if( !jQuery('#btnChkboxLegal').is(':checked')) {
                errorTxt += "- Debe Aceptar las bases legales<br>";
            }

            if( errorTxt != "" ){
                errorTxt = '<div class="erroresTxt">' + errorTxt + "</div>";
                var cuerpoErrores = '<div class="titleErroresSuscrip">Para continuar arregle los siguientes errores</div>';
                var cierreErrores = '<div class="btnRect" id="btnSuscribirVolver">Volver</div>';
                jQuery("#popUpNewsletter>div.contenidoPopError").html(cuerpoErrores + errorTxt + cierreErrores);
                jQuery("#btnSuscribirVolver").click(volverFormu);
                jQuery("#popUpNewsletter>div.contenidoPop").fadeOut("fast", function(){jQuery("#popUpNewsletter>div.contenidoPopError").fadeIn("slow");});

            }else{

                sendFormu();
            }
        }

        function sendFormu(){//_params){
            // mostramos contenidoPopEnviando
            jQuery("#popUpNewsletter>div.contenidoPop").fadeOut("fast", function(){jQuery("#popUpNewsletter>div.contenidoPopEnviando").fadeIn("fast");});

            jQuery.ajax({
                url: '/',
                type: 'post',
                dataType: 'json',
                data: jQuery('form#mailchimp-signup-subscribe-block-mailchimpform-form').serialize(),
                success: function(data) {
                    registroOk();
                },
                error: function(data){
                    console.log("Error " + data.status);
                    if (data.status == "200"){
                        registroOk();
                    }else{
                        jQuery("#popUpNewsletter>div.contenidoPopEnviando").fadeOut("fast", function(){jQuery("#popUpNewsletter>div.contenidoPopError").fadeIn("fast");});
                    }
                }
            });

        }

        function registroOk(){
            jQuery("#popUpNewsletter>div.contenidoPopEnviando").fadeOut("fast", function(){jQuery("#popUpNewsletter>div.contenidoPopResult").fadeIn("fast");});
        }

        function volverFormu(){
            jQuery("#popUpNewsletter>div.contenidoPopError").fadeOut("slow", function(){ jQuery("#popUpNewsletter>div.contenidoPop").fadeIn("fast");}); }

        function esMail(email) {
            var re = /^([\w-]+(?:\.[\w-]+)*)@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$/i;
            return re.test(email);
        }

        function abreNewsSuscription(){
            var altoPan = jQuery(window).height();

            if( jQuery("#popUpNewsletter").css("height") == "100%"){
                jQuery("#popUpNewsletter").css("margin-top","0");

            }else{
                if( altoPan < jQuery("#popUpNewsletter").height() ){
                    jQuery("#popUpNewsletter").css("margin-top","0");
                }else{
                    jQuery("#popUpNewsletter").css("margin-top",((altoPan - jQuery("#popUpNewsletter").height())/2)+"px");
                }
            }
            jQuery("#popUpNewsletterWrapper").fadeIn("fast");
        }

        function cierraPopNewsletter(e){
            var targetId = jQuery(e.target).attr("id");
            if( targetId == "popUpNewsletterWrapper" || targetId == "btnCerrarNewsletter"  || targetId == "popNewsletterClose" ){
                jQuery("#popUpNewsletterWrapper").fadeOut("fast",  function(){
                        jQuery("#popUpNewsletter>div.contenidoPopResult").fadeOut("fast");
                        jQuery("#popUpNewsletter>div.contenidoPopEnviando").fadeOut("fast");
                        jQuery("#popUpNewsletter>div.contenidoPopError").fadeOut("fast");
                        jQuery("#popUpNewsletter>div.contenidoPop").fadeIn("fast");
                    }
                );
            }
        }
    });
</script><!-- UNUSED -->

